class Clock extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      date:new Date()
    }
  }

  componentDidMount(){
     this.timerID = setInterval( () => tick, 100);
  }

  componentWillUnmount(){
     clearInterval(this.timerID);
  }

render() {
  return (
      <div>
        <h1>Hello, world!</h1>
        <h2>It is {this.state.date.toLocaleTimeString()}.</h2>
      </div>
   ) 
  };

}

